    private void calBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_calBtnActionPerformed
     alturaImp.getText();
     float altura = Float.parseFloat(alturaImp.getText());
        System.out.println(altura);
       
        pesoImp.getText();
     float peso = Float.parseFloat(pesoImp.getText());
        System.out.println(peso);
        
        float imc;
        imc = peso / (altura*altura);
                
        valorImc.setText(String.valueOf(imc));
        System.out.println(imc);
        
        if (imc <= 17){
            tipoImc.setText(String.valueOf("MUITO ABAIXO DO PESO"));
        } else if (imc > 17 && imc < 18.50f){
            tipoImc.setText(String.valueOf("ABAIXO DO PESO"));
        } else if (imc >= 18.50f && imc < 25f){
            tipoImc.setText(String.valueOf("PESO NORMAL"));
        }else if (imc >= 25 && imc < 30){
            tipoImc.setText(String.valueOf("ACIMA DO PESO"));
        }else if (imc >= 30 && imc < 35){
            tipoImc.setText(String.valueOf("OBESIDADE I"));
        }else if (imc >= 35 && imc < 40){
            tipoImc.setText(String.valueOf("OBESIDADE II (SEVERA)"));
        }else if (imc >= 40){
            tipoImc.setText(String.valueOf("OBESIDADE III (MÃ“RBIDA)"));
        }
    }//GEN-LAST:event_calBtnActionPerformed

